package main

import "os"

// XOR key used for obfuscation
const XorKey = byte(0x42)

// XOR-encoded "Mozilla/5.0 (Windows NT 10.0; Win64; x64) Safari/537.36"
// Exported to prevent dead code elimination
var EncodedUA = []byte{
	0x0f, 0x2d, 0x38, 0x2b, 0x2e, 0x2e, 0x23, 0x6d, 0x77, 0x6c,
	0x72, 0x62, 0x6a, 0x15, 0x2b, 0x2c, 0x26, 0x2d, 0x35, 0x31,
	0x62, 0x0c, 0x16, 0x62, 0x73, 0x72, 0x6c, 0x72, 0x79, 0x62,
	0x15, 0x2b, 0x2c, 0x74, 0x76, 0x79, 0x62, 0x3a, 0x74, 0x76,
	0x6b, 0x62, 0x11, 0x23, 0x24, 0x23, 0x30, 0x2b, 0x6d, 0x77,
	0x71, 0x75, 0x6c, 0x71, 0x74,
}

//go:noinline
func decodeXOR(data []byte, key byte) string {
	result := make([]byte, len(data))
	for i, b := range data {
		result[i] = b ^ key
	}
	return string(result)
}

func main() {
	ua := decodeXOR(EncodedUA, XorKey)
	os.Stdout.WriteString(ua + "\n")
}
